import { useRef } from 'react';
import { isDefined } from '../utils';
export default function useCachedValue(getter) {
    const ref = useRef();
    const currentValue = ref.current;
    if (isDefined(currentValue)) {
        return () => currentValue;
    }
    return () => {
        const value = getter();
        ref.current = value;
        return value;
    };
}
